package com.sillylife.realchat.views.fragmentsimport android.os.Bundleimport android.widget.Toastimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport com.sillylife.realchat.Rimport com.sillylife.realchat.util.CommonUtilimport com.sillylife.realchat.util.FragmentHelperopen class BaseFragment : Fragment() {    var mFragmentManager: FragmentManager? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        //initEveryThing()        mFragmentManager = fragmentManager    }    override fun onDestroy() {        super.onDestroy()    }    override fun onResume() {        super.onResume()    }    override fun onPause() {        super.onPause()        //dieEveryThing()    }    fun showToast(message: String, length: Int) {        Toast.makeText(activity, message, length).show()    }    fun addFragment(fragment: Fragment, tag: String?) {        if (tag != null && !CommonUtil.textIsEmpty(tag)) {            FragmentHelper.add(R.id.container, mFragmentManager!!, fragment, tag)        } else {            FragmentHelper.add(R.id.container, mFragmentManager!!, fragment)        }    }    fun replaceFragment(fragment: Fragment, tag: String) {        FragmentHelper.replace(R.id.container, mFragmentManager!!, fragment, tag)    }}